createOrganizationContract = function(
  _orgName,
  _orgType,
  _orgAddress,
  _adminName,
  _adminPK,
  _approverPK,
  _onSuccess,
  _onFailure
) {
  //abi
  let abi = [
    {
      inputs: [
        { name: "_orgName", type: "string" },
        { name: "_orgType", type: "string" },
        { name: "_orgAddr", type: "string" },
        { name: "_adminName", type: "string" },
        { name: "_adminPK", type: "string" },
        { name: "_approverPK", type: "string" }
      ],
      payable: true,
      stateMutability: "payable",
      type: "constructor"
    },
    {
      anonymous: false,
      inputs: [
        { indexed: false, name: "_time", type: "uint256" },
        { indexed: false, name: "_orgId", type: "bytes32" },
        { indexed: false, name: "_orgName", type: "string" },
        { indexed: false, name: "_orgType", type: "string" },
        { indexed: false, name: "_orgAddr", type: "string" },
        { indexed: false, name: "_adminName", type: "string" },
        { indexed: false, name: "_adminPK", type: "string" },
        { indexed: false, name: "_approverPK", type: "string" }
      ],
      name: "OrganizationCreated",
      type: "event"
    }
  ];

  //options {data, arguments}
  let options = {
    data:
      "",
    arguments: [
      _orgName,
      _orgType,
      _orgAddress,
      _adminName,
      _adminPK,
      _approverPK
    ]
  };

  //contract
  var contract = new web3.eth.Contract(abi, null, options);

  //deploy the contract
  contract
    .deploy(options)
    .send({
      from: web3.eth.accounts[0],
      gas: 4700000
    })
    .then(function(contractInstance) {
      if (contractInstance) {
        _onSuccess(contractInstance);
      } else {
        _onFailure();
      }
    });
};
